/* eslint-disable */

export function paginate(items, pageNumber, pageSize) {
    const startIndex = (pageNumber - 1) * pageSize;
    return [...items].splice(startIndex, pageSize);
}

export function renderPhrase (number){
    const lastOne = Number(number.toString().slice(-1));
    if (number > 4 && number < 15) {
        return "человек тусанет";
    }
    if (lastOne === 1) return "человек тусанет";
    if ([2, 3, 4].indexOf(lastOne) >= 0) return "человека тусанут";
    return "человек тусанет";
};


const arrayOne = [1, 2, 123, 128, 14, 1115, 1282, 1288, 114, 1112, 10103]

const result = arrayOne.map( (numberProps) => {
    const lastDigit = Number(numberProps.toString().slice(-1))
    const lastTwoDigit = Number(numberProps.toString().slice(-2))
    //console.log(2, lastTwoDigit)

     if ([2,3,4].includes(lastDigit)){

         [12, 13, 14].map((item) => {
             (lastTwoDigit === item)
                 ? console.log(" 1 человек", numberProps, lastTwoDigit)
                 : console.log(" 2 человека", numberProps, lastTwoDigit)
         })
     }

     else {
         console.log("3 человек", numberProps)
     }

})
